const topics = [
  {topic:'AI & Automation', desc:'How artificial intelligence is transforming workplace productivity and efficiency in 2026'},
  {topic:'Remote Work Culture', desc:'Building and maintaining strong team dynamics in a distributed workforce environment'},
  {topic:'Digital Transformation', desc:'Strategic technology adoption for competitive advantage in modern business landscape'},
  {topic:'Professional Development', desc:'Continuous learning and career advancement in the rapidly evolving tech industry'},
  {topic:'Innovation & Leadership', desc:'Navigating organizational change and leading teams through digital disruption'}
];
const selected = topics[Math.floor(Math.random() * topics.length)];
return [{json: {topic: selected.topic, description: selected.desc}}];

return [{
